gsap.utils.toArray(".rael-stacking-card").forEach((card,index,cards)=>{const baseX=parseFloat(card.dataset.translateX)||0;const baseY=parseFloat(card.dataset.translateY)||0;const baseRotate=parseFloat(card.dataset.rotate)||0;const baseScale=parseFloat(card.dataset.scale)||1;const baseBlur=parseFloat(card.dataset.blur)||0;const baseGrayscale=parseFloat(card.dataset.grayscale)||0;if(index===cards.length-1)return;ScrollTrigger.create({trigger:card,start:"bottom top",onEnter:()=>{gsap.to(card,{x:baseX+index*5,y:baseY+index*10,rotate:baseRotate+10,scale:baseScale-index*0.02,filter:`blur(${baseBlur + index * 2}px) grayscale(${
          baseGrayscale * 100 + index * 20
        }%)`,opacity:1,overwrite:"auto",duration:0.4,ease:"power2.out",})},onLeaveBack:()=>{gsap.to(card,{x:baseX,y:baseY,rotate:baseRotate,scale:baseScale,filter:`blur(0px) grayscale(${baseGrayscale * 100}%)`,opacity:1,overwrite:"auto",clearProps:"transform",duration:0.4,ease:"power2.out",})},})})